$.fn.inlineEditor=function(){$(this).each(function(){var a=$(this);a.html('<span class="display" style="cursor: pointer;">'+a.html()+"</span>");a.prepend('<input type="text" class="edit" style="display: none;"/>');a.append('<span class="spinner" style="display: none;"><i class="fa fa-spin fa-spinner"></i> </span>');a.find(".display:first").click(function(){a.find(".edit").show();a.find(".edit").focus();a.find(".edit-btn, .display").hide();a.find(".edit").val(a.find(".display:first").text())});a.find(".edit:first").change(function(){if(a.data("current-save-action")!==undefined&&a.data("current-save-action")!==null){return}a.find(".spinner").show();var c=a.attr("data-field-name");var b={};b[c]=a.find(".edit").val();$.ajax({url:a.attr("data-url"),data:b,type:"POST",dataType:"json",success:function(){a.find(".spinner").hide();a.find(".edit").hide();a.find(".display").show();a.find(".display").html(a.find(".edit").val())},error:function(){a.data("current-save-action",setTimeout(function(){a.data("current-save-action",null);a.find(".edit:first").trigger("change")},5000))}})})})};